-- Инициализация данных для элементов упражнений
TRUNCATE exercises;

INSERT INTO exercises (id, slug, name, description, execution_technique, progression, muscle_groups, common_mistakes, training_tips, image_path)
VALUES
-- Стойка на руках
(gen_random_uuid()::text,
    'handstand',
    'Стойка на руках',
    'Стойка на руках — это базовый гимнастический элемент, который требует силы, баланса и координации. Это фундаментальное упражнение, которое открывает путь к более сложным элементам, таким как отжимания в стойке на руках, спичаг и другие.',
    E'1. Начните с позиции стоя, ноги на ширине плеч.\n2. Поставьте руки на пол на ширине плеч, пальцы направлены вперед.\n3. Поднимите одну ногу вверх, затем оттолкнитесь второй ногой.\n4. Поднимите обе ноги вертикально вверх, держа их вместе.\n5. Держите тело прямым, напрягите пресс и ягодицы.\n6. Смотрите между руками или немного вперед.\n7. Балансируйте, используя пальцы рук для коррекции положения.',
    E'1. Стойка у стены — начните с выполнения стойки у стены для развития силы и привыкания к перевернутому положению.\n2. Стойка с поддержкой — попросите партнера помочь удерживать равновесие.\n3. Стойка с отталкиванием от стены — отталкивайтесь от стены и пытайтесь удержать баланс самостоятельно.\n4. Стойка на руках — финальный этап, выполнение без поддержки.',
    E'Основные мышечные группы:\n- Плечи (дельтовидные мышцы)\n- Трицепсы\n- Предплечья\n- Пресс\n- Ягодичные мышцы\n- Мышцы спины (для стабилизации)',
    E'1. Согнутые локти — держите руки прямыми, это обеспечивает стабильность.\n2. Прогиб в спине — напрягайте пресс и ягодицы, чтобы держать тело прямым.\n3. Разведенные ноги — держите ноги вместе для лучшего баланса.\n4. Неправильное положение головы — не запрокидывайте голову слишком сильно назад.\n5. Слабые пальцы рук — активно используйте пальцы для балансировки.',
    E'1. Тренируйтесь регулярно, но не переусердствуйте — давайте мышцам время на восстановление.\n2. Используйте стену для начальных тренировок — это безопасно и эффективно.\n3. Развивайте силу плеч и кора — это основа для стойки на руках.\n4. Практикуйте баланс каждый день — даже по 5-10 минут.\n5. Не бойтесь падать — используйте мягкие маты и учитесь правильно группироваться.\n6. Разминайтесь перед тренировкой — особенно запястья и плечи.',
    '/images/handstand.png'
),
-- Отжимания в стойке на руках
(gen_random_uuid()::text,
    'handstand-pushups',
    'Отжимания в стойке на руках',
    'Отжимания в стойке на руках — это продвинутое упражнение, которое требует значительной силы плеч, трицепсов и кора. Это один из самых впечатляющих элементов в калистенике, демонстрирующий невероятную силу верхней части тела.',
    E'1. Примите стойку на руках у стены для безопасности.\n2. Медленно согните руки в локтях, опуская голову к полу.\n3. Опускайтесь до тех пор, пока голова почти не коснется пола.\n4. Держите локти близко к телу, не разводите их в стороны.\n5. Напрягите пресс и ягодицы для стабилизации.\n6. Мощно оттолкнитесь руками, возвращаясь в исходное положение.\n7. Контролируйте движение на всех этапах выполнения.',
    E'1. Негативные отжимания — медленно опускайтесь вниз, затем вставайте на ноги.\n2. Отжимания у стены — выполняйте полные отжимания с опорой на стену.\n3. Отжимания с частичной амплитудой — уменьшите глубину опускания.\n4. Отжимания в стойке на руках — полное выполнение без поддержки.',
    E'Основные мышечные группы:\n- Плечи (передние и средние дельты)\n- Трицепсы\n- Предплечья\n- Пресс\n- Верхняя часть груди\n- Трапециевидные мышцы',
    E'1. Разведение локтей в стороны — держите локти близко к телу.\n2. Недостаточная глубина — опускайтесь достаточно низко для полной амплитуды.\n3. Потеря баланса — контролируйте положение тела на всех этапах.\n4. Слишком быстрый темп — выполняйте движение медленно и подконтрольно.\n5. Недостаточная разминка — обязательно разминайте плечи и запястья.',
    E'1. Сначала освоите уверенную стойку на руках — это основа упражнения.\n2. Развивайте силу плеч и трицепсов отдельными упражнениями.\n3. Начинайте с негативных повторений — они безопаснее и эффективнее.\n4. Используйте стену для поддержки на начальных этапах.\n5. Тренируйтесь 2-3 раза в неделю, давая мышцам время на восстановление.\n6. Не форсируйте прогресс — это может привести к травмам.',
    '/images/handstand_pushups.png'
),
-- Планш в шпагате
(gen_random_uuid()::text,
    'straddle-planche',
    'Планш в шпагате',
    'Планш в шпагате — это промежуточный этап на пути к полному планшу. Это упражнение требует огромной силы плеч, предплечий и кора, а также гибкости в тазобедренных суставах.',
    E'1. Начните с упора лежа, руки на ширине плеч.\n2. Перенесите вес тела вперед, поднимая ноги от пола.\n3. Разведите ноги в стороны в шпагат.\n4. Держите тело параллельно полу, руки прямые.\n5. Напрягите пресс и ягодицы для стабилизации.\n6. Держите голову в нейтральном положении.\n7. Удерживайте позицию максимально долго.',
    E'1. Лягушка (frog stand) — базовое упражнение для развития баланса и силы.\n2. Планш с согнутыми ногами — упрощенная версия с подтянутыми коленями.\n3. Планш в шпагате — промежуточный этап с разведенными ногами.\n4. Полный планш — финальный этап с выпрямленными и сведенными ногами.',
    E'Основные мышечные группы:\n- Плечи (передние дельты)\n- Предплечья\n- Трицепсы\n- Пресс\n- Ягодичные мышцы\n- Мышцы спины\n- Внутренняя поверхность бедер (для шпагата)',
    E'1. Слишком широкий хват — держите руки на ширине плеч или уже.\n2. Прогиб в спине — напрягайте пресс для прямого положения тела.\n3. Согнутые локти — держите руки полностью прямыми.\n4. Недостаточный перенос веса вперед — центр тяжести должен быть над руками.\n5. Напряжение в шее — держите голову расслабленной.',
    E'1. Развивайте силу плеч и предплечий — это ключевые мышцы для планша.\n2. Работайте над гибкостью тазобедренных суставов для шпагата.\n3. Тренируйте лягушку до уверенного выполнения 30+ секунд.\n4. Используйте прогрессию постепенно — не перескакивайте этапы.\n5. Тренируйтесь 3-4 раза в неделю с достаточным отдыхом.\n6. Разминайте запястья перед каждой тренировкой.',
    '/images/straddle_planche.png'
),
-- Полный планш
(gen_random_uuid()::text,
    'full-planche',
    'Полный планш',
    'Полный планш — это один из самых сложных элементов в калистенике. Он требует невероятной силы плеч, предплечий, кора и идеальной техники. Это вершина мастерства в работе с собственным весом.',
    E'1. Начните с упора лежа, руки на ширине плеч или уже.\n2. Перенесите вес тела вперед, поднимая ноги от пола.\n3. Держите ноги вместе и выпрямленными.\n4. Тело должно быть параллельно полу.\n5. Руки полностью прямые, локти заблокированы.\n6. Напрягите все мышцы кора для стабилизации.\n7. Удерживайте позицию максимально долго.',
    E'1. Лягушка (frog stand) — базовое упражнение для развития баланса.\n2. Планш с согнутыми ногами — упрощенная версия.\n3. Планш в шпагате — промежуточный этап.\n4. Полный планш — финальный этап с выпрямленными и сведенными ногами.',
    E'Основные мышечные группы:\n- Плечи (передние дельты) — основная нагрузка\n- Предплечья — критически важны для удержания\n- Трицепсы\n- Пресс — для стабилизации\n- Ягодичные мышцы\n- Мышцы спины\n- Квадрицепсы',
    E'1. Слишком широкий хват — для полного планша нужен узкий хват.\n2. Прогиб в спине — тело должно быть абсолютно прямым.\n3. Согнутые локти — руки должны быть полностью прямыми.\n4. Разведенные ноги — ноги должны быть вместе.\n5. Недостаточный перенос веса — центр тяжести строго над руками.\n6. Недостаточная сила — не пытайтесь выполнить без должной подготовки.',
    E'1. Развивайте силу плеч и предплечий годами — это долгий путь.\n2. Освойте все предыдущие этапы прогрессии до совершенства.\n3. Тренируйте планш 4-5 раз в неделю с достаточным отдыхом.\n4. Используйте статические удержания и динамические упражнения.\n5. Работайте над гибкостью запястий и плеч.\n6. Будьте терпеливы — полный планш может занять годы тренировок.\n7. Разминайтесь тщательно перед каждой тренировкой.',
    '/images/full_planche.png'
),
-- Спичаг
(gen_random_uuid()::text,
    'spichag',
    'Спичаг',
    'Спичаг — это динамический элемент, переход из стойки на руках в упор лежа и обратно. Это комбинация силы, баланса, координации и контроля тела. Один из самых зрелищных элементов в калистенике.',
    E'1. Начните в стойке на руках.\n2. Медленно начните наклонять тело вперед.\n3. Одновременно сгибайте руки в локтях.\n4. Контролируйте опускание, перенося вес вперед.\n5. Опуститесь в упор лежа, касаясь пола грудью.\n6. Мощно оттолкнитесь руками, возвращаясь в стойку.\n7. Контролируйте движение на всех этапах.',
    E'1. Стойка на руках — базовое упражнение, которое нужно освоить в совершенстве.\n2. Медленный спуск из стойки — контролируемое опускание в упор лежа.\n3. Спичаг с опорой на стену — для безопасности на начальных этапах.\n4. Полный спичаг — выполнение без поддержки.',
    E'Основные мышечные группы:\n- Плечи (все пучки дельт)\n- Трицепсы\n- Предплечья\n- Пресс\n- Грудные мышцы\n- Мышцы спины\n- Ягодичные мышцы',
    E'1. Недостаточный контроль — движение должно быть плавным и контролируемым.\n2. Резкое падение — не позволяйте телу падать, контролируйте спуск.\n3. Неправильное положение рук — руки должны быть на ширине плеч.\n4. Потеря баланса — контролируйте центр тяжести на всех этапах.\n5. Недостаточная сила — убедитесь, что у вас достаточно силы для выполнения.',
    E'1. Освойте стойку на руках до совершенства — это основа спичага.\n2. Тренируйте медленный спуск из стойки — это ключевой элемент.\n3. Развивайте силу плеч, трицепсов и кора.\n4. Используйте стену для поддержки на начальных этапах.\n5. Тренируйтесь 3-4 раза в неделю с достаточным отдыхом.\n6. Разминайтесь тщательно перед каждой тренировкой.\n7. Не форсируйте прогресс — спичаг требует времени и терпения.',
    '/images/spichag.png'
) ON CONFLICT (slug) DO NOTHING;

CREATE OR REPLACE VIEW exercises_display AS
SELECT id::text AS id, slug, name, description, execution_technique, progression, muscle_groups, common_mistakes, training_tips, image_path
FROM exercises;
##END_SCRIPT##
